import React from 'react';
import { FcLike } from 'react-icons/fc';
import { toast } from 'react-toastify';

const Card = (props) => {
  let course = props.course;
  let likedCourses = props.likedCourses;          // variable to track the liked courses(initially empty array)
  let setLikedCourses = props.setLikedCourses;

  function clickHandler() {
    if (likedCourses.includes(course.id)) {        //if the course is already liked  
      //agr pehle se like hua pda h to usse unlike kro
      setLikedCourses((prev) => prev.filter((cid) => (cid !== course.id)));
      toast.warning("Liked removed")
    }
    else {
      // agr pehle se like nahi h ye course to insert krna h ye course liked course me
      if (likedCourses.length === 0) {
        setLikedCourses([course.id]);
      }
      else {
        // non-empty pehle se h to insert prev + new course.id
        setLikedCourses((prev) => [...prev, course.id]);
      }
      toast.success("Liked Successfully");
    }
  }

  return (
    <div className='w-[300px] bg-bgDark bg-opacity-80 rounded-md overflow-hidden'>

      <div className='relative'>

        <img src={course.image.url}></img>

        <div className='w-[40px] h-[40px] bg-white rounded-full absolute right-2 bottom-3 flex justify-center items-center'>
          <button onClick={clickHandler}> <FcLike fontSize="1.75rem" /> </button>
        </div>

      </div>

      <div className='p-4'>
        <p className='text-white font-semibold text-lg leading-6'>{course.title}</p>
        <p className='mt-2 text-white'>{course.description}</p>
      </div>

    </div>
  )
}

export default Card
