import React, { useEffect, useState } from "react";
import {apiUrl,filterData} from "./data";
import Navbar from "./components/Navbar";
import Filter from "./components/Filter";
import Cards from "./components/Cards";
import { toast } from "react-toastify";

const App = () => {    

  const [courses,setCourse] = useState(null);
  const [loading,setLoading] = useState(true);

  //Function to fetch data from api
  async function fetchData() {
    setLoading(true); // jb tk data fetch ho rha h tb tk loading ho
    try{
      const response = await fetch(apiUrl); //api call to get the data
      const fetchedData = await response.json();   // convert api data into json format
      //save data into a variable
      setCourse(fetchedData); 
    }
    catch(error){
      toast.error('Something went wrong');
    }
    setLoading(false); // data fetch hone k baad loading ruk jaye.
  }

  // Run on first render
  useEffect(()=>{  
    fetchData();
  },[]);

  return (
    <>

      <div> <Navbar/> </div>

      <div> <Filter filterData={filterData} /> </div>

      <div>
        {
          loading ? (<Spinner/>) : (<Cards courses={courses} />)   // agr loading true h to loading dikhao wrna card dikhao
        }
      </div>


    </>
  );
};

export default App;
