import React from 'react'
import Card from './Card'

const Cards = ({ courses }) => {
    let allCourses = [];

    //returns you a list of all courses recieved from api response
    const getCourses = () => { // inserting all the data into a single array
        Object.values(courses).forEach((it) => {          //getting only VALUE from key-values pair and for each value running forEach loop
            it.forEach((course) => {                      // getting every single value of VALUE and inserting into a single array
                allCourses.push(course)
            })
        })
        return allCourses;
    }
    return (
        <div>
            {
                 getCourses().map((it)=>{
                    <Card> </Card>
                })
            }
           
        </div>
    )
}

export default Cards
